sp_widget.client_script:
function(cabrillo, $http, $location, $scope) {
	/* widget controller */
	var c = this;
	c.currentUser = $scope.user.first_name;
	c.isNative = cabrillo.isNative();
	c.serialNumber = "";
	c.showForm = false;
	c.showHomeButtons = true;
	c.itemDetails = '';
	c.asset = '';
	c.error = false;
	c.errorMessage = "";

	// Client side access to camera scan
	c.scan = function(){
		cabrillo.camera.getBarcode().then(function(barcode) {
			if (barcode){
				window.location.href = 'asset_scan?id=scanned_information&serial_no=' + barcode;
			}else{
				c.error = true;
				c.errorMessage = "Try Again";
			}
		}, function(error) {
			c.error = true;
			c.errorMessage = "Unable to scan.";
		});
	};

	c.manualSubmit = function(serialNumber){
		window.location.href = '/asset_scan?id=scanned_information&serial_no=' + serialNumber;
	};

	c.showManualInput = function(){
		//Show the input field and label
		c.showForm = true;
		//Hide Get Started Header and the Scanning button 
		c.showHomeButtons = false;
		//Add hash to URL
		var manualHash = $location.hash("manual-hash");
		//Hide error message if already loaded
		c.error = false;
	};

	c.goHome = function() {
		c.showForm = false;
		c.showHomeButtons = true;
		var homeHash = $location.hash('');
	};

	function getAsset(serial){
		$http({
			method: 'get',
			url: '/api/x_gege9_asset_scan/asset_scanner/GetAssetInfo/' + serial,
			headers: {
				'Content-Type': 'application/json'
			}
		}).then(function(response){
			if(response.statusText === 'OK'){
				c.error = false;
				c.asset = response.result;
			}else{
				c.error = true;

				if(!!response.errorMessage){
					c.errorMessage = response.errorMessage;
				}
			}
		}, function(response){
			c.error = true;
			c.errorMessage = "Unable to find this serial number. Try entering manually.";
		});
	}

}

