sys_script_include.script:
var AssetToDo = Class.create();
AssetToDo.prototype = {
    initialize: function(){
    },

	create: function(asset, type, shortDescription, order){
		var toDoGR = new GlideRecord('x_gege9_asset_scan_todo');
		toDoGR.initialize();
		toDoGR.asset = asset;
		toDoGR.state = 1;
		toDoGR.active = true;
		toDoGR.type = type;
		toDoGR.short_description = shortDescription;
		toDoGR.order = order;
		return toDoGR.insert();
	},

	createFreeForm: function(asset, shortDescription){
		var toDoGR = new GlideRecord('x_gege9_asset_scan_todo');
		toDoGR.addQuery('asset', asset);
		toDoGR.orderByDesc('order');
		toDoGR.query();
		if(toDoGR.hasNext()){
			toDoGR.next();
			return this.create(asset, 100, shortDescription, toDoGR.order + 1);
		}else{
			return this.create(asset, 100, shortDescription, 0);
		}
	},

	createReclamation: function(asset, order){
		return this.create(asset, 200, 
					"The previous owner has requested Data Reclamation." + 
						   " Tap here to scan the asset into the Data Reclamation bin.", order);
	},

	createLegalHold: function(asset, order){
		return this.create(asset, 300, 
					"This device is on Legal Hold. Tap here to scan the asset" +
						   " into the Legal Hold bin.", order);
	},

	createReceived: function(asset, order){
		return this.create(asset, 500, 
					"Tap here to scan the asset into the depot.", order);
	},

	createWipe: function(asset, order){
		return this.create(asset, 400, 
					"Tap here to scan the asset into the Data Wipe bin.", order);
	},

	createToDoList: function(asset, dataReclamation, legalHold){
		var order = 0;
		this.createReceived(asset, order++);
		if(dataReclamation){
			this.createReclamation(asset, order++);
		}
		if(legalHold){
			this.createLegalHold(asset, order++);
		}
		if(!legalHold){
			this.createWipe(asset, order++);
		}
	},

	getToDoList: function(asset){
		var toDoList = [];
		var toDoGR = new GlideRecord('x_gege9_asset_scan_todo');
		toDoGR.addQuery('asset', asset);
		toDoGR.orderBy('order');
		toDoGR.query();
		while(toDoGR.next()){
			toDoList.push({
				type: toDoGR.type.getDisplayValue(),
				state: toDoGR.state.toString(),
				shortDescription: toDoGR.short_description.toString(),
				order: toDoGR.order.toString(),
				sysId: toDoGR.sys_id.toString()
			});
		}
		return toDoList;
	},

	markComplete: function(todo){
		var toDoGR = new GlideRecord('x_gege9_asset_scan_todo');
		toDoGR.addQuery('sys_id', todo);
		toDoGR.query();
		toDoGR.next();
		toDoGR.state = 3;
		if(toDoGR.update()){
			return true;
		}else{
			return false;
		}
	},

	markSkipped: function(todo){
		var toDoGR = new GlideRecord('x_gege9_asset_scan_todo');
		toDoGR.addQuery('sys_id', todo);
		toDoGR.query();
		toDoGR.next();
		toDoGR.state = 7;
		if(toDoGR.update()){
			return true;
		}else{
			return false;
		}
	},

    type: 'AssetToDo'
};
