sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var assetGR, responseObj,
		identifier = request.pathParams.Indentifier;


	assetGR = new GlideRecord('alm_hardware');
	assetGR.addQuery('install_status', '!=', '7');
	assetGR.addEncodedQuery('asset_tag=' + identifier + '^ORserial_number=' + identifier);
	assetGR.query();
	if(assetGR.hasNext()){
		assetGR.next();

		responseObj = {
			sysId: assetGR.sys_id,
			serialNumber: assetGR.serial_number.toString(),
			state: assetGR.install_status.getDisplayValue(),
			substate: assetGR.substatus.getDisplayValue(),
			stockroom: assetGR.stockroom.getDisplayValue(),
			modelImage: assetGR.model.picture.getDisplayValue(),
			modelCategory: assetGR.model_category.getDisplayValue(),
			model: assetGR.model.getDisplayValue(),
			acquisitionCountry: assetGR.u_acquisition_country.getDisplayValue(),
			retirementDate: assetGR.retirement_date.getDisplayValue(),
			assetType: assetGR.acquisition_method.toString(),
			assignedTo: assetGR.assigned_to.user_name.toString(),
			reservedFor: assetGR.reserved_for.toString(),
			assignedToIsVIP: assetGR.assigned_to.vip.toString(),
			url: "/" + assetGR.getTableName() + ".do?sys_id=" + assetGR.sys_id.toString()
		};

		if (assetGR.assigned_to.user_name){
			responseObj.legalHold = checkAtlas(assetGR.assigned_to.user_name);
			updateLegalHold(assetGR.sys_id, responseObj.legalHold);
		} else if (assetGR.reserved_for.user_name){
			responseObj.legalHold = checkAtlas(assetGR.reserved_for.user_name);
			updateLegalHold(assetGR.sys_id, responseObj.legalHold);
		} else {
			responseObj.legalHold = assetGR.u_legal_hold.toString();
		}

		if (responseObj.legalHold === 'Atlas error'){
			responseObj.legalHold = assetGR.u_legal_hold.toString();
		}

		response.setBody(responseObj);
		response.setStatus(200);
		return response;
	}else{
		var error = new sn_ws_err.ServiceError();
		error.setStatus(404);
		error.setMessage('No asset for this Serial Number.');
		return error;
	}

	//Check Atlas for legal hold	
	function checkAtlas(userSSO){
		try { 
			//get the SSO associated with the asset
			//Get response from Atlas
			var r = new sn_ws.RESTMessageV2('x_gege9_asset_scan.Atlas Onhold Check', 'Default GET');
			r.setStringParameterNoEscape('SSO', userSSO);
			var instance = gs.getProperty('instance_name');
			var midserver = gs.getProperty('ge.mid.server.' + instance);
			r.setMIDServer(midserver);
			var result = r.execute();
			var httpStatus = result.getStatusCode();
			var atlasResponse = JSON.parse(result.getBody());
			var atlasFormatter;
			if (atlasResponse.hold === 'no'){
				atlasFormatter = 'False';
			} else if (atlasResponse.hold === 'yes'){
				atlasFormatter = 'True';
			}
			return atlasFormatter;
		}
		catch(ex) {
			var message = ex.getMessage();
			return 'Atlas error';
		}
	}

	function updateLegalHold(assetSysId, legalHold){
		var error;
		var assetGR = new GlideRecord('alm_hardware');
		assetGR.addQuery('sys_id', assetSysId);
		assetGR.query();
		if(assetGR.hasNext()){
			assetGR.next();
			if (assetGR.u_legal_hold !== legalHold) {
				assetGR.u_legal_hold = legalHold;
				assetGR.update();
			}
		} else {
			return 'Legal Hold Update Error';
		}

	}


})(request, response);
