sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var assetGR, responseObj,
		identifier = request.pathParams.Indentifier;


	assetGR = new GlideRecord('alm_hardware');
	var orQuery = assetGR.addEncodedQuery('ci.u_alias=' + identifier + '^ORserial_number=' + identifier);
	assetGR.query();
	if(assetGR.hasNext()){
		assetGR.next();
		var itemArray = [];
		var configItem = assetGR.ci.sys_id; 
		var ritmGR = new GlideRecord('sc_req_item');
		ritmGR.addEncodedQuery('active=true^cat_item.name=PC Return^ORcat_item.name=PC Deployment^variables.6016c545db0f0f00262950a45e961914=' +
						configItem + '^ORvariables.1b1315c5db4f0f00262950a45e961956=' + configItem);
		ritmGR.query();
		if(ritmGR.hasNext()){
			while(ritmGR.next()){
				itemArray.push({
					itemNumber: ritmGR.number,
					item: ritmGR.cat_item.getDisplayValue(),
					stage: ritmGR.stage.getDisplayValue(),
					dataReclamation: ritmGR.variables.v_data_reclamation ? ritmGR.variables.v_data_reclamation : 'Not specified',
					url: "/sc_req_item.do?sys_id=" + ritmGR.sys_id
				});
			}
		}
		responseObj = {
			sysId: assetGR.sys_id,
			modelImage: assetGR.model.picture.getDisplayValue(),
			modelCategory: assetGR.model_category.getDisplayValue(),
			model: assetGR.model.getDisplayValue(),
			acquisitionCountry: assetGR.u_acquisition_country,
			retirementDate: assetGR.retirement_date.getDisplayValue(),
			assetType: assetGR.acquisition_method,
			assignedTo: assetGR.assigned_to.user_name,
			assignedToIsVIP: assetGR.assigned_to.vip,
			url: "/" + assetGR.getTableName() + ".do?sys_id=" + assetGR.sys_id.toString(),
			items: itemArray
		};
		response.setBody(responseObj);
		response.setStatus(200);
		return response;
	}else{
		var error = new sn_ws_err.ServiceError();
		error.setStatus(404);
		error.setMessage('No asset for this Serial Number.');
		return error;
	}


})(request, response);
