sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var error, NewStockroom = request.pathParams.NewStockroom;

	var stockroomGR = new GlideRecord('alm_stockroom');
	stockroomGR.addEncodedQuery('sys_id=' + NewStockroom + '^ORname=' + NewStockroom);
	stockroomGR.query();
	if(stockroomGR.hasNext()){
		stockroomGR.next();
		//if(gs.getUser().isMemberOf(stockroomGR.assignment_group.toString()) || stockroomGR.manager.sys_id.toString() === gs.getUserID() || stockroomGR.assignment_group ==''){
		var assetGR = new GlideRecord('alm_asset'); //BKB 3/3/19 why is this using alm_asset instead of alm_hardware like everything else?
		assetGR.addQuery('sys_id', request.pathParams.Asset);
		assetGR.query();
		if(assetGR.hasNext()){
			assetGR.next();
			assetGR.install_status = 6; //in stock
			assetGR.substatus = 'available';
			assetGR.stockroom = stockroomGR.sys_id;
			assetGR.reserved_for = assetGR.assigned_to;
			if (assetGR.update() != null){
				//Update the CI states
				var ciGR = new GlideRecord('cmdb_ci_pc_hardware');
				//ciGR.addEncodedQuery('serial_number=' + identifier);
				ciGR.addQuery('sys_id', assetGR.ci.toString());
				ciGR.query();
				if(ciGR.hasNext()){
					ciGR.next();
					ciGR.install_status = 6; //in stock
					ciGR.operational_status = 1; //operational
					ciGR.u_verification_status = 301; // trusted
					ciGR.update();
				}

				response.setBody({ 
					name: stockroomGR.name, 
					details: "Item has moved to new stockroom."
				});
				response.setStatus(200);
				return response;
			}else{
				error = new sn_ws_err.ServiceError();
				error.setStatus(404);
				error.setMessage('Nah Brah');
				return error;
			}
		}else{
			error = new sn_ws_err.ServiceError();
			error.setStatus(404);
			error.setMessage('This asset is not valid.');
			return error;
		}
	}else{
		error = new sn_ws_err.ServiceError();
		error.setStatus(404);
		error.setMessage('This stockroom is not valid.');
		return error;
	}
//	}else{
//		error = new sn_ws_err.ServiceError();
//		error.setStatus(404);
//		error.setMessage('This stockroom is not valid.');
//		return error;
//	}	

})(request, response);
