sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var error;

	var stockroomGR = new GlideRecord('alm_stockroom');
	stockroomGR.addQuery('sys_id', request.pathParams.NewStockroom);
	stockroomGR.query();
	if(stockroomGR.hasNext()){
		stockroomGR.next();
		var assetGR = new GlideRecord('alm_asset');
		assetGR.addQuery('sys_id', request.pathParams.Asset);
		assetGR.query();
		if(assetGR.hasNext()){
			assetGR.next();
			// Put the asset in stock.
			assetGR.install_status = 6;
			assetGR.stockroom = stockroomGR.sys_id;
			if (assetGR.update() != null){
				response.setBody({ 
					name: stockroomGR.name, 
					details: "Item has moved to new stockroom."
				});
				response.setStatus(200);
				return response;
			}else{
				error = new sn_ws_err.ServiceError();
				error.setStatus(404);
				error.setMessage('Nah Brah');
				return error;
			}
		}else{
			error = new sn_ws_err.ServiceError();
			error.setStatus(404);
			error.setMessage('This asset is not valid.');
			return error;
		}
	}else{
		error = new sn_ws_err.ServiceError();
		error.setStatus(404);
		error.setMessage('This stockroom is not valid.');
		return error;
	}


})(request, response);
