sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var assetGR, assets = [],
	identifier = request.pathParams.Identifier;

	assetGR = new GlideRecord('alm_hardware');
	assetGR.setLimit(25);
	assetGR.orderByDesc('sys_updated_on');
	assetGR.addEncodedQuery('serial_numberSTARTSWITH'+ identifier +'^ORasset_tagSTARTSWITH' + identifier);
	assetGR.query();
	// Check to see if any asset exists
	if(assetGR.hasNext()){
		while(assetGR.next()){
			if(assetGR.asset_tag.getDisplayValue().startsWith(identifier)){
				assets.push({identifier: assetGR.asset_tag.getDisplayValue(), model: assetGR.model.getDisplayValue()});
			}

			if(assetGR.serial_number.getDisplayValue().startsWith(identifier)){
				assets.push({
					identifier:assetGR.serial_number.getDisplayValue(),
					model: assetGR.model.getDisplayValue()
				});
			}
		}
		// Asset was not found send error
	}else{
		var error = new sn_ws_err.ServiceError();
		error.setStatus(404);
		error.setMessage('No asset for this Serial Number.');
		return error;
	}

	response.setBody(assets);
	return response;


	})(request, response);
